SELECT cliente.CLI_ID, cliente.PES_CPF, cliente.PES_NOME, telefone.TEL_DDI, telefone.TEL_DDD_NUMERO, telefone.TEL_ID FROM TBL_CLIENTE as cliente INNER JOIN TBL_CLIENTE_HAS_TBL_TELEFONE as cliTemTel ON cliente.CLI_ID = cliTemTel.CLI_ID_TBL_CLIENTE INNER JOIN TBL_TELEFONE as telefone ON cliTemTel.TEL_ID_TBL_TELEFONE = telefone.TEL_ID;

SELECT cliente.CLI_ID, cliente.PES_CPF, cliente.PES_NOME, aluguel.ALU_ID, aluguel.QUA_ID_TBL_QUADRA, aluguel.ALU_DURACAO, horario.HOR_ID, horario.HOR_INICIO, dia.DIA_NOME FROM TBL_CLIENTE as cliente INNER JOIN TBL_ALUGUEL as aluguel ON cliente.CLI_ID = aluguel.CLI_ID_TBL_CLIENTE INNER JOIN TBL_HORARIO as horario ON aluguel.ALU_ID = horario.ALU_ID_TBL_ALUGUEL INNER JOIN TBL_HORARIO_HAS_TBL_DIA as horaTemDia ON horario.HOR_ID = horaTemDia.HOR_ID_TBL_HORARIO INNER JOIN TBL_DIA as dia ON horaTemDia.DIA_ID_TBL_DIA = dia.DIA_ID;

SELECT cliente.CLI_ID, cliente.PES_CPF, cliente.PES_NOME, turma.TUR_ID, turma.TUR_DURACAO, horario.HOR_INICIO, dia.DIA_NOME, dia.DIA_ID FROM TBL_CLIENTE AS cliente INNER JOIN TBL_CLIENTE_HAS_TBL_TURMA AS cliTemTur ON cliente.CLI_ID = cliTemTur.CLI_ID_TBL_CLIENTE INNER JOIN TBL_TURMA AS turma ON cliTemTur.TUR_ID_TBL_TURMA = turma.TUR_ID INNER JOIN TBL_HORARIO AS horario ON turma.TUR_ID = horario.TUR_ID_TBL_TURMA INNER JOIN TBL_HORARIO_HAS_TBL_DIA as horaTemDia ON horario.HOR_ID = horaTemDia.HOR_ID_TBL_HORARIO INNER JOIN TBL_DIA AS dia ON horaTemDia.DIA_ID_TBL_DIA = dia.DIA_ID;

SELECT cliente.CLI_ID, cliente.PES_CPF, cliente.PES_NOME, turma.TUR_ID, aula.QUA_ID_TBL_QUADRA, aula.AUL_ID, professor.PES_NOME, professor.COLAB_MATRICULA FROM TBL_CLIENTE AS cliente INNER JOIN TBL_CLIENTE_HAS_TBL_TURMA AS cliTemTur ON cliente.CLI_ID = cliTemTur.CLI_ID_TBL_CLIENTE INNER JOIN TBL_TURMA AS turma ON cliTemTur.TUR_ID_TBL_TURMA = turma.TUR_ID INNER JOIN TBL_AULA AS aula ON turma.TUR_ID = aula.TUR_ID_TBL_TURMA INNER JOIN TBL_COLABORADOR_HAS_TBL_AULA AS colabTemAula ON aula.AUL_ID = colabTemAula.AUL_ID_TBL_AULA INNER JOIN TBL_COLABORADOR AS professor ON colabTemAula.COLAB_MATRICULA_TBL_COLABORADOR = professor.COLAB_MATRICULA;

